import WalletService from './wallet-service';
import { Connection, PublicKey, Transaction, SystemProgram } from '@solana/web3.js';
import { createTransferInstruction, TOKEN_PROGRAM_ID } from '@solana/spl-token';

class TokenService {
  constructor() {
    this.connection = null;
    this.tokenInfo = {};
  }

  initialize(endpoint) {
    this.connection = new Connection(endpoint, 'confirmed');
  }

  async getTokenBalance(tokenAddress, walletAddress) {
    if (!this.connection) throw new Error('Token service not initialized');
    if (!walletAddress) throw new Error('Wallet address is required');

    try {
      const tokenPublicKey = new PublicKey(tokenAddress);
      const walletPublicKey = new PublicKey(walletAddress);

      // Get token account info
      const tokenAccounts = await this.connection.getParsedTokenAccountsByOwner(
        walletPublicKey,
        { mint: tokenPublicKey }
      );

      // If no token accounts found, balance is 0
      if (tokenAccounts.value.length === 0) return 0;

      // Get balance from the first token account
      const balance = tokenAccounts.value[0].account.data.parsed.info.tokenAmount.uiAmount;
      return balance;
    } catch (error) {
      console.error('Error getting token balance:', error);
      throw error;
    }
  }

  async transferToken(tokenAddress, fromWallet, toAddress, amount) {
    if (!this.connection) throw new Error('Token service not initialized');
    if (!WalletService.isConnected()) throw new Error('Wallet not connected');

    try {
      const tokenPublicKey = new PublicKey(tokenAddress);
      const toPublicKey = new PublicKey(toAddress);

      // Get the token account of the fromWallet address for the token
      const fromTokenAccounts = await this.connection.getParsedTokenAccountsByOwner(
        new PublicKey(fromWallet),
        { mint: tokenPublicKey }
      );

      // If no token account found, throw error
      if (fromTokenAccounts.value.length === 0) {
        throw new Error('No token account found for the sender');
      }

      const fromTokenAccount = fromTokenAccounts.value[0].pubkey;

      // Get or create associated token account for recipient
      let toTokenAccount;
      try {
        const toTokenAccounts = await this.connection.getParsedTokenAccountsByOwner(
          toPublicKey,
          { mint: tokenPublicKey }
        );

        if (toTokenAccounts.value.length > 0) {
          toTokenAccount = toTokenAccounts.value[0].pubkey;
        } else {
          // In a real implementation, you would create an associated token account
          // This is simplified for the example
          throw new Error('Recipient does not have a token account for this token');
        }
      } catch (error) {
        throw new Error('Error checking recipient token account: ' + error.message);
      }

      // Create transfer instruction - Updated to use createTransferInstruction
      const transferInstruction = createTransferInstruction(
        fromTokenAccount,
        toTokenAccount,
        new PublicKey(fromWallet),
        amount * Math.pow(10, this.tokenInfo[tokenAddress]?.decimals || 9),
        [],
        TOKEN_PROGRAM_ID
      );

      // Create transaction and add the transfer instruction
      const transaction = new Transaction().add(transferInstruction);

      // Set recent blockhash and fee payer
      transaction.recentBlockhash = (await this.connection.getRecentBlockhash()).blockhash;
      transaction.feePayer = new PublicKey(fromWallet);

      // Sign and send transaction
      const signedTransaction = await WalletService.signTransaction(transaction);
      const signature = await this.connection.sendRawTransaction(signedTransaction.serialize());

      // Wait for confirmation
      await this.connection.confirmTransaction(signature);

      return {
        signature,
        status: 'confirmed'
      };
    } catch (error) {
      console.error('Error transferring tokens:', error);
      throw error;
    }
  }

  async getTokenInfo(tokenAddress) {
    if (!this.connection) throw new Error('Token service not initialized');

    try {
      // If we already have the info cached, return it
      if (this.tokenInfo[tokenAddress]) {
        return this.tokenInfo[tokenAddress];
      }

      const tokenPublicKey = new PublicKey(tokenAddress);
      const tokenInfo = await this.connection.getParsedAccountInfo(tokenPublicKey);

      if (!tokenInfo.value) {
        throw new Error('Token not found');
      }

      // Parse token info
      const parsedInfo = {
        address: tokenAddress,
        decimals: tokenInfo.value.data.parsed.info.decimals,
        supply: tokenInfo.value.data.parsed.info.supply,
        name: tokenInfo.value.data.parsed.info.name || 'Unknown Token',
        symbol: tokenInfo.value.data.parsed.info.symbol || 'UNKNOWN'
      };

      // Cache the info
      this.tokenInfo[tokenAddress] = parsedInfo;

      return parsedInfo;
    } catch (error) {
      console.error('Error getting token info:', error);
      throw error;
    }
  }

  async getAccountTransactions(walletAddress, limit = 10) {
    if (!this.connection) throw new Error('Token service not initialized');
    if (!walletAddress) throw new Error('Wallet address is required');

    try {
      const walletPublicKey = new PublicKey(walletAddress);

      // Get recent transactions for the account
      const signatures = await this.connection.getSignaturesForAddress(
        walletPublicKey,
        { limit }
      );

      // Return simplified transaction data
      return signatures.map(sig => ({
        signature: sig.signature,
        timestamp: sig.blockTime || Date.now() / 1000,
        status: sig.confirmationStatus || 'confirmed',
        type: 'unknown',
        symbol: 'SOL',
        decimals: 9,
        amount: 0 // We don't parse the amount here for simplicity
      }));
    } catch (error) {
      console.error('Error getting account transactions:', error);
      return []; // Return empty array instead of throwing
    }
  }

  async getTokenTransactions(tokenAddress, walletAddress, limit = 10) {
    if (!this.connection) throw new Error('Token service not initialized');
    if (!tokenAddress) throw new Error('Token address is required');
    if (!walletAddress) throw new Error('Wallet address is required');

    try {
      // Just return empty array for now to avoid errors
      return [];
    } catch (error) {
      console.error('Error getting token transactions:', error);
      return []; // Return empty array instead of throwing
    }
  }

  async getNativeBalance(walletAddress) {
    if (!this.connection) throw new Error('Token service not initialized');
    if (!walletAddress) throw new Error('Wallet address is required');

    try {
      const walletPublicKey = new PublicKey(walletAddress);
      const balance = await this.connection.getBalance(walletPublicKey);
      return balance / 1000000000; // Convert lamports to SOL
    } catch (error) {
      console.error('Error getting native balance:', error);
      throw error;
    }
  }

  async transferNativeSOL(fromWallet, toAddress, amount) {
    if (!this.connection) throw new Error('Token service not initialized');
    if (!WalletService.isConnected()) throw new Error('Wallet not connected');

    try {
      const toPublicKey = new PublicKey(toAddress);
      const fromPublicKey = new PublicKey(fromWallet);

      // Create a simple transfer instruction
      const transaction = new Transaction().add(
        SystemProgram.transfer({
          fromPubkey: fromPublicKey,
          toPubkey: toPublicKey,
          lamports: amount * 1000000000 // Convert SOL to lamports
        })
      );

      // Set recent blockhash and fee payer
      transaction.recentBlockhash = (await this.connection.getRecentBlockhash()).blockhash;
      transaction.feePayer = fromPublicKey;

      // Sign and send transaction
      const signedTransaction = await WalletService.signTransaction(transaction);
      const signature = await this.connection.sendRawTransaction(signedTransaction.serialize());

      // Wait for confirmation
      await this.connection.confirmTransaction(signature);

      return {
        signature,
        status: 'confirmed'
      };
    } catch (error) {
      console.error('Error transferring SOL:', error);
      throw error;
    }
  }
}

export default new TokenService();
