import WalletService from './wallet-service';
import { Connection, PublicKey, Transaction, SystemProgram, LAMPORTS_PER_SOL } from '@solana/web3.js';
import { createTransferInstruction, TOKEN_PROGRAM_ID, createBurnInstruction } from '@solana/spl-token';
import { clusterApiUrl } from '@solana/web3.js';
import { getAssociatedTokenAddress, getMint } from '@solana/spl-token';

class TokenService {
  constructor() {
    this.connection = null;
    this.tokenInfo = {};
  }

  initialize(endpoint) {
    this.connection = new Connection(endpoint, 'confirmed');
  }

  async getTokenBalance(tokenAddress, walletAddress) {
    if (!this.connection) throw new Error('Token service not initialized');
    if (!walletAddress) throw new Error('Wallet address is required');

    try {
      const tokenPublicKey = new PublicKey(tokenAddress);
      const walletPublicKey = new PublicKey(walletAddress);

      // Get token account info
      const tokenAccounts = await this.connection.getParsedTokenAccountsByOwner(
        walletPublicKey,
        { mint: tokenPublicKey }
      );

      // If no token accounts found, balance is 0
      if (tokenAccounts.value.length === 0) return 0;

      // Get balance from the first token account
      const balance = tokenAccounts.value[0].account.data.parsed.info.tokenAmount.uiAmount;
      return balance;
    } catch (error) {
      console.error('Error getting token balance:', error);
      throw error;
    }
  }

  async transferToken(tokenAddress, fromWallet, toAddress, amount) {
    if (!this.connection) throw new Error('Token service not initialized');

    try {
      const tokenPublicKey = new PublicKey(tokenAddress);
      const toPublicKey = new PublicKey(toAddress);
      const fromPublicKey = new PublicKey(fromWallet);

      // Calculate tax amounts (5% total)
      const liquidityTax = amount * 0.03; // 3% for liquidity
      const marketingTax = amount * 0.01; // 1% for marketing
      const charityTax = amount * 0.01; // 1% for charity
      const burnTax = amount * 0.01; // 1% for burn

      // Calculate amount after tax
      const amountAfterTax = amount - (liquidityTax + marketingTax + charityTax + burnTax);

      // Get token decimals
      const decimals = this.tokenInfo[tokenAddress]?.decimals || 
                      (await this.getTokenInfo(tokenAddress)).decimals;

      // Create transaction
      const transaction = new Transaction();

      // Get the token account of the fromWallet address for the token
      const fromTokenAccounts = await this.connection.getParsedTokenAccountsByOwner(
        fromPublicKey,
        { mint: tokenPublicKey }
      );

      // If no token account found, throw error
      if (fromTokenAccounts.value.length === 0) {
        throw new Error('No token account found for the sender');
      }

      const fromTokenAccount = fromTokenAccounts.value[0].pubkey;

      // Get or create associated token account for recipient
      let toTokenAccount;
      try {
        const toTokenAccounts = await this.connection.getParsedTokenAccountsByOwner(
          toPublicKey,
          { mint: tokenPublicKey }
        );

        if (toTokenAccounts.value.length > 0) {
          toTokenAccount = toTokenAccounts.value[0].pubkey;
        } else {
          // In a real implementation, you would create an associated token account
          // This is simplified for the example
          throw new Error('Recipient does not have a token account for this token');
        }
      } catch (error) {
        throw new Error('Error checking recipient token account: ' + error.message);
      }

      // Create transfer instruction
      const transferInstruction = createTransferInstruction(
        fromTokenAccount,
        toTokenAccount,
        fromPublicKey,
        Number(Math.floor(amountAfterTax * Math.pow(10, decimals))),
        [],
        TOKEN_PROGRAM_ID
      );

      // Add transfer instruction to transaction
      transaction.add(transferInstruction);

      // Add burn instruction for the burn tax
      if (burnTax > 0) {
        // Create burn instruction
        const burnInstruction = createBurnInstruction(
          fromTokenAccount,
          tokenPublicKey,
          fromPublicKey,
          Number(Math.floor(burnTax * Math.pow(10, decimals)))
        );

        // Add burn instruction to transaction
        transaction.add(burnInstruction);
      }

      // Set recent blockhash and fee payer
      transaction.recentBlockhash = (await this.connection.getRecentBlockhash()).blockhash;
      transaction.feePayer = fromPublicKey;

      // Sign transaction using the wallet adapter
      let signedTransaction;

      // For Phantom
      if (window.solana?.isPhantom) {
        signedTransaction = await window.solana.signTransaction(transaction);
      }
      // For Solflare
      else if (window.solflare) {
        signedTransaction = await window.solflare.signTransaction(transaction);
      }
      else {
        throw new Error('No compatible wallet found');
      }

      const signature = await this.connection.sendRawTransaction(signedTransaction.serialize());

      // Wait for confirmation
      await this.connection.confirmTransaction(signature);

      return {
        signature,
        status: 'confirmed',
        amountBurned: burnTax
      };
    } catch (error) {
      console.error('Error transferring token:', error);
      throw error;
    }
  }

  async getTokenInfo(tokenAddress) {
    if (!this.connection) throw new Error('Token service not initialized');

    try {
      // If we already have the info cached, return it
      if (this.tokenInfo[tokenAddress]) {
        return this.tokenInfo[tokenAddress];
      }

      const tokenPublicKey = new PublicKey(tokenAddress);
      const tokenInfo = await this.connection.getParsedAccountInfo(tokenPublicKey);

      if (!tokenInfo.value) {
        throw new Error('Token not found');
      }

      // Parse token info
      const parsedInfo = {
        address: tokenAddress,
        decimals: tokenInfo.value.data.parsed.info.decimals,
        supply: tokenInfo.value.data.parsed.info.supply,
        name: tokenInfo.value.data.parsed.info.name || 'Unknown Token',
        symbol: tokenInfo.value.data.parsed.info.symbol || 'UNKNOWN'
      };

      // Cache the info
      this.tokenInfo[tokenAddress] = parsedInfo;

      return parsedInfo;
    } catch (error) {
      console.error('Error getting token info:', error);
      throw error;
    }
  }

  async getAccountTransactions(walletAddress, limit = 10) {
    if (!this.connection) throw new Error('Token service not initialized');
    if (!walletAddress) throw new Error('Wallet address is required');

    try {
      const walletPublicKey = new PublicKey(walletAddress);

      // Get recent transactions for the account
      const signatures = await this.connection.getSignaturesForAddress(
        walletPublicKey,
        { limit }
      );

      // Return simplified transaction data
      return signatures.map(sig => ({
        signature: sig.signature,
        timestamp: sig.blockTime || Date.now() / 1000,
        status: sig.confirmationStatus || 'confirmed',
        type: 'unknown',
        symbol: 'SOL',
        decimals: 9,
        amount: 0 // We don't parse the amount here for simplicity
      }));
    } catch (error) {
      console.error('Error getting account transactions:', error);
      return []; // Return empty array instead of throwing
    }
  }

  async getTokenTransactions(tokenAddress, walletAddress, limit = 10) {
    if (!this.connection) throw new Error('Token service not initialized');
    if (!tokenAddress) throw new Error('Token address is required');
    if (!walletAddress) throw new Error('Wallet address is required');

    try {
      // Just return empty array for now to avoid errors
      return [];
    } catch (error) {
      console.error('Error getting token transactions:', error);
      return []; // Return empty array instead of throwing
    }
  }

  async getNativeBalance(walletAddress) {
    if (!this.connection) throw new Error('Token service not initialized');
    if (!walletAddress) throw new Error('Wallet address is required');

    try {
      const walletPublicKey = new PublicKey(walletAddress);
      const balance = await this.connection.getBalance(walletPublicKey);
      return balance / 1000000000; // Convert lamports to SOL
    } catch (error) {
      console.error('Error getting native balance:', error);
      throw error;
    }
  }

  async transferNativeSOL(fromWallet, toAddress, amount) {
    if (!this.connection) throw new Error('Token service not initialized');

    try {
      const toPublicKey = new PublicKey(toAddress);
      const fromPublicKey = new PublicKey(fromWallet);

      // Check balance before transfer
      const balance = await this.connection.getBalance(fromPublicKey);
      const transferAmount = amount * LAMPORTS_PER_SOL;
      const estimatedFee = 5000; // Approximate fee in lamports

      if (balance < transferAmount + estimatedFee) {
        throw new Error('Insufficient balance for transfer and fees');
      }

      // Create a simple transfer instruction
      const transaction = new Transaction().add(
        SystemProgram.transfer({
          fromPubkey: fromPublicKey,
          toPubkey: toPublicKey,
          lamports: transferAmount
        })
      );

      // Get the latest blockhash
      const { blockhash, lastValidBlockHeight } = await this.connection.getLatestBlockhash();
      transaction.recentBlockhash = blockhash;
      transaction.feePayer = fromPublicKey;

      // Get the wallet adapter
      const walletAdapter = window.solana || window.solflare;
      if (!walletAdapter) {
        throw new Error('Please connect Phantom or Solflare wallet');
      }

      try {
        // Sign and send transaction
        const signed = await walletAdapter.signTransaction(transaction);
        const signature = await this.connection.sendRawTransaction(signed.serialize());

        // Wait for confirmation with specific commitment and timeout
        const confirmation = await this.connection.confirmTransaction({
          signature,
          blockhash,
          lastValidBlockHeight
        }, 'confirmed');

        if (confirmation.value.err) {
          throw new Error('Transaction failed: ' + JSON.stringify(confirmation.value.err));
        }

        return {
          signature,
          status: 'confirmed'
        };
      } catch (signError) {
        console.error('Transaction signing error:', signError);
        if (signError.message.includes('User rejected')) {
          throw new Error('Transaction was rejected by the user');
        }
        throw new Error('Failed to sign transaction: ' + signError.message);
      }
    } catch (error) {
      console.error('SOL transfer error:', error);
      // Provide more specific error messages
      if (error.message.includes('insufficient')) {
        throw new Error('Insufficient balance for transfer and network fees');
      } else if (error.message.includes('rejected')) {
        throw new Error('Transaction was rejected by the user');
      } else if (error.message.includes('blockhash')) {
        throw new Error('Network error: Please try again');
      }
      throw new Error('Transfer failed: ' + error.message);
    }
  }

  async burnToken(tokenAddress, ownerAddress, amount) {
    if (!this.connection) throw new Error('Token service not initialized');

    try {
      const tokenPublicKey = new PublicKey(tokenAddress);
      const ownerPublicKey = new PublicKey(ownerAddress);

      // Get the token account of the owner
      const tokenAccounts = await this.connection.getParsedTokenAccountsByOwner(
        ownerPublicKey,
        { mint: tokenPublicKey }
      );

      if (tokenAccounts.value.length === 0) {
        throw new Error('No token account found for the owner');
      }

      const tokenAccount = tokenAccounts.value[0].pubkey;

      // Get token decimals
      const decimals = this.tokenInfo[tokenAddress]?.decimals || 
                      (await this.getTokenInfo(tokenAddress)).decimals;

      // Create burn instruction
      const burnInstruction = createBurnInstruction(
        tokenAccount,
        tokenPublicKey,
        ownerPublicKey,
        amount * Math.pow(10, decimals)
      );

      // Create transaction and add the burn instruction
      const transaction = new Transaction().add(burnInstruction);

      // Set recent blockhash and fee payer
      transaction.recentBlockhash = (await this.connection.getRecentBlockhash()).blockhash;
      transaction.feePayer = ownerPublicKey;

      // Sign transaction using the wallet adapter
      let signedTransaction;

      // For Phantom
      if (window.solana?.isPhantom) {
        signedTransaction = await window.solana.signTransaction(transaction);
      }
      // For Solflare
      else if (window.solflare) {
        signedTransaction = await window.solflare.signTransaction(transaction);
      }
      else {
        throw new Error('No compatible wallet found');
      }

      const signature = await this.connection.sendRawTransaction(signedTransaction.serialize());

      // Wait for confirmation
      await this.connection.confirmTransaction(signature);

      return {
        signature,
        status: 'confirmed'
      };
    } catch (error) {
      console.error('Error burning token:', error);
      throw error;
    }
  }
}

export default new TokenService();

export const burnTokens = async (wallet, tokenAddress, amount) => {
  if (!wallet.publicKey) {
    throw new Error('Wallet not connected');
  }

  const connection = new Connection(
    clusterApiUrl(process.env.REACT_APP_SOLANA_NETWORK || 'devnet'),
    'confirmed'
  );

  // For SOL burning (send to a dead address)
  if (tokenAddress === 'native') {
    // Burn address (a known unusable address)
    const burnAddress = new PublicKey('1111111111111111111111111111111111111111111');

    const transaction = new Transaction().add(
      SystemProgram.transfer({
        fromPubkey: wallet.publicKey,
        toPubkey: burnAddress,
        lamports: amount * LAMPORTS_PER_SOL,
      })
    );

    const signature = await wallet.sendTransaction(transaction, connection);
    await connection.confirmTransaction(signature, 'confirmed');
    return signature;
  } else {
    // For SPL tokens
    const mint = new PublicKey(tokenAddress);
    const associatedTokenAddress = await getAssociatedTokenAddress(
      mint,
      wallet.publicKey
    );

    // Get mint info to get decimals
    const mintInfo = await getMint(connection, mint);

    const burnInstruction = createBurnInstruction(
      associatedTokenAddress,
      mint,
      wallet.publicKey,
      amount * Math.pow(10, mintInfo.decimals)
    );

    const transaction = new Transaction().add(burnInstruction);

    const signature = await wallet.sendTransaction(transaction, connection);
    await connection.confirmTransaction(signature, 'confirmed');
    return signature;
  }
};
