import React, { useState, useEffect } from 'react';
import { useWallet } from '../contexts/WalletContext';
import WalletConnect from './WalletConnect';
import TokenBalance from './TokenBalance';
import TokenTransfer from './TokenTransfer';
import TransactionHistory from './TransactionHistory';
import BurnToken from './BurnToken';
import TokenService from '../services/token-service';

const Dashboard = () => {
  const { connected, currentAccount } = useWallet();
  const [activeTab, setActiveTab] = useState('wallet');
  const [tokenAddress, setTokenAddress] = useState('native'); // Default to native SOL
  const [selectedToken, setSelectedToken] = useState('SOL');

  // Initialize token service
  useEffect(() => {
    const network = process.env.REACT_APP_SOLANA_NETWORK || 'devnet';
    const endpoint = process.env.REACT_APP_SOLANA_RPC_URL || 
                      `https://api.${network}.solana.com`;

    TokenService.initialize(endpoint);
  }, []);

  const handleTokenSelection = (e) => {
    const value = e.target.value;
    if (value === 'SOL') {
      setTokenAddress('native');
      setSelectedToken('SOL');
    } else if (value === 'custom') {
      setTokenAddress('');
      setSelectedToken('custom');
    }
  };

  const handleCustomTokenAddress = (e) => {
    setTokenAddress(e.target.value);
  };

  const renderTabContent = () => {
    switch (activeTab) {
      case 'wallet':
        return <TokenBalance />;
      case 'transfer':
        return <TokenTransfer tokenAddress={tokenAddress} />;
      case 'burn':
        return <BurnToken tokenAddress={tokenAddress} />;
      case 'history':
        return <TransactionHistory tokenAddress={tokenAddress} />;
      default:
        return <TokenBalance />;
    }
  };

  return (
    <div className="dashboard-container">
      <header className="p-3 mb-4 border-bottom">
        <div className="container">
          <div className="d-flex flex-wrap align-items-center justify-content-between">
            <div className="d-flex align-items-center">
              <h1 className="h4 me-3 mb-0">Alebrije Wallet</h1>
              <span className="badge bg-warning text-dark">
                {process.env.REACT_APP_SOLANA_NETWORK || 'devnet'}
              </span>
            </div>

            <WalletConnect />
          </div>
        </div>
      </header>

      <main className="container py-4">
        {!connected ? (
          <div className="text-center p-5">
            <h2>Connect Your Wallet</h2>
            <p className="lead">
              Connect your Solana wallet to access the Alebrije token functionalities.
            </p>
          </div>
        ) : (
          <>
            <ul className="nav nav-tabs mb-4">
              <li className="nav-item">
                <button 
                  className={`nav-link ${activeTab === 'wallet' ? 'active' : ''}`}
                  onClick={() => setActiveTab('wallet')}
                >
                  Wallet
                </button>
              </li>
              <li className="nav-item">
                <button 
                  className={`nav-link ${activeTab === 'transfer' ? 'active' : ''}`}
                  onClick={() => setActiveTab('transfer')}
                >
                  Transfer
                </button>
              </li>
              <li className="nav-item">
                <button 
                  className={`nav-link ${activeTab === 'burn' ? 'active' : ''}`}
                  onClick={() => setActiveTab('burn')}
                >
                  Burn
                </button>
              </li>
              <li className="nav-item">
                <button 
                  className={`nav-link ${activeTab === 'history' ? 'active' : ''}`}
                  onClick={() => setActiveTab('history')}
                >
                  History
                </button>
              </li>
            </ul>

            {(activeTab === 'transfer' || activeTab === 'burn') && (
              <div className="row mb-4">
                <div className="col">
                  <div className="card">
                    <div className="card-body">
                      <h5 className="card-title">Select Token</h5>
                      <div className="mb-3">
                        <select 
                          className="form-select mb-3" 
                          value={selectedToken} 
                          onChange={handleTokenSelection}
                        >
                          <option value="SOL">SOL (Native)</option>
                          <option value="custom">Custom Token</option>
                        </select>

                        {selectedToken === 'custom' && (
                          <div className="input-group">
                            <span className="input-group-text">Token Address</span>
                            <input
                              type="text"
                              className="form-control"
                              value={tokenAddress}
                              onChange={handleCustomTokenAddress}
                              placeholder="Enter token mint address"
                            />
                          </div>
                        )}
                      </div>
                      <small className="text-muted d-block">
                        {activeTab === 'transfer' 
                          ? 'Select SOL for native transfers or enter a token mint address for SPL tokens'
                          : 'Select SOL for native burns or enter a token mint address for SPL tokens'}
                      </small>
                    </div>
                  </div>
                </div>
              </div>
            )}

            <div className="row">
              <div className="col">
                {renderTabContent()}
              </div>
            </div>
          </>
        )}
      </main>
    </div>
  );
};

export default Dashboard;
