import React, { useEffect, useState } from 'react';
import { useWallet } from '../contexts/WalletContext';

const WalletConnectionTest = () => {
  const { connected, accounts, connectWallet, disconnectWallet, loading, error } = useWallet();
  const [testResult, setTestResult] = useState(null);

  const runConnectionTest = async () => {
    try {
      setTestResult({ status: 'running', message: 'Testing wallet connection...' });

      // Test connection
      const connected = await connectWallet();

      if (connected) {
        setTestResult({ 
          status: 'success', 
          message: 'Wallet connected successfully!',
          data: { accounts }
        });
      } else {
        setTestResult({ 
          status: 'error', 
          message: 'Failed to connect wallet'
        });
      }
    } catch (err) {
      setTestResult({ 
        status: 'error', 
        message: `Error: ${err.message}`,
        error: err
      });
    }
  };

  return (
    <div className="wallet-test-container p-4 border rounded">
      <h3>Wallet Connection Test</h3>

      <div className="mb-3">
        <p>Status: {connected ? 'Connected' : 'Disconnected'}</p>
        {connected && accounts.length > 0 && (
          <p>Account: {accounts[0]}</p>
        )}
      </div>

      <div className="d-flex gap-2 mb-3">
        <button 
          className="btn btn-primary" 
          onClick={runConnectionTest}
          disabled={loading}
        >
          {loading ? 'Connecting...' : 'Test Connection'}
        </button>

        {connected && (
          <button 
            className="btn btn-outline-danger" 
            onClick={disconnectWallet}
          >
            Disconnect
          </button>
        )}
      </div>

      {error && (
        <div className="alert alert-danger">
          {error.message || 'An error occurred'}
        </div>
      )}

      {testResult && (
        <div className={`alert ${testResult.status === 'success' ? 'alert-success' : 
                              testResult.status === 'error' ? 'alert-danger' : 
                              'alert-info'}`}>
          <p>{testResult.message}</p>
          {testResult.data && (
            <pre className="mt-2 bg-light p-2 rounded">
              {JSON.stringify(testResult.data, null, 2)}
            </pre>
          )}
        </div>
      )}
    </div>
  );
};

export default WalletConnectionTest;
