import React, { useState, useEffect } from 'react';
import { useWallet } from '../contexts/WalletContext';
import WalletConnectionTest from './WalletConnectionTest';
import TokenTransfer from './TokenTransfer';
import { clusterApiUrl } from '@solana/web3.js';
import TokenService from '../services/token-service';

const Dashboard = () => {
  const { connected, accounts, currentAccount } = useWallet();
  const [activeTab, setActiveTab] = useState('wallet');
  const [tokenAddress, setTokenAddress] = useState('');

  // Initialize token service
  useEffect(() => {
    const endpoint = clusterApiUrl('devnet');
    TokenService.initialize(endpoint);
  }, []);

  const renderTabContent = () => {
    switch (activeTab) {
      case 'wallet':
        return <WalletConnectionTest />;
      case 'transfer':
        return <TokenTransfer tokenAddress={tokenAddress} />;
      default:
        return <WalletConnectionTest />;
    }
  };

  return (
    <div className="dashboard-container p-4">
      <div className="row mb-4">
        <div className="col">
          <h2>Alebrije Wallet Dashboard</h2>
          {connected && (
            <p className="text-muted">
              Connected: {currentAccount?.slice(0, 6)}...{currentAccount?.slice(-4)}
            </p>
          )}
        </div>
      </div>

      <div className="row mb-4">
        <div className="col">
          <ul className="nav nav-tabs">
            <li className="nav-item">
              <button 
                className={`nav-link ${activeTab === 'wallet' ? 'active' : ''}`}
                onClick={() => setActiveTab('wallet')}
              >
                Wallet
              </button>
            </li>
            <li className="nav-item">
              <button 
                className={`nav-link ${activeTab === 'transfer' ? 'active' : ''}`}
                onClick={() => setActiveTab('transfer')}
                disabled={!connected}
              >
                Transfer Tokens
              </button>
            </li>
          </ul>
        </div>
      </div>

      {activeTab === 'transfer' && connected && (
        <div className="row mb-4">
          <div className="col">
            <div className="input-group">
              <span className="input-group-text">Token Address</span>
              <input
                type="text"
                className="form-control"
                value={tokenAddress}
                onChange={(e) => setTokenAddress(e.target.value)}
                placeholder="Enter token mint address"
              />
            </div>
            <small className="text-muted">
              Enter the mint address of the token you want to transfer
            </small>
          </div>
        </div>
      )}

      <div className="row">
        <div className="col">
          {renderTabContent()}
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
