import React, { createContext, useContext, useState, useEffect, useCallback } from 'react';
import { ConnectionProvider, WalletProvider, useWallet as useSolanaWallet } from '@solana/wallet-adapter-react';
import { WalletAdapterNetwork } from '@solana/wallet-adapter-base';
import { 
  PhantomWalletAdapter, 
  SolflareWalletAdapter,
  TorusWalletAdapter,
  LedgerWalletAdapter,
  CloverWalletAdapter,
  SlopeWalletAdapter,
  SolletWalletAdapter,
  SolletExtensionWalletAdapter,
  CoinbaseWalletAdapter
} from '@solana/wallet-adapter-wallets';
import { WalletModalProvider, WalletMultiButton } from '@solana/wallet-adapter-react-ui';
import { clusterApiUrl } from '@solana/web3.js';
import { AlebrijeError, ErrorTypes } from '../utils/error-handler';

// Import wallet adapter CSS
import '@solana/wallet-adapter-react-ui/styles.css';

// Create the context
const WalletContext = createContext(null);

export function WalletContextProvider({ children }) {
  // Network configuration - allow switching between networks
  const [network, setNetwork] = useState(WalletAdapterNetwork.Devnet);
  const endpoint = clusterApiUrl(network);

  // Wallet state
  const [accounts, setAccounts] = useState([]);
  const [currentAccount, setCurrentAccount] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // Get all supported wallet adapters
  const wallets = [
    new PhantomWalletAdapter(),
    new SolflareWalletAdapter(),
    new TorusWalletAdapter(),
    new LedgerWalletAdapter(),
    new CloverWalletAdapter(),
    new SlopeWalletAdapter(),
    new SolletWalletAdapter(),
    new SolletExtensionWalletAdapter(),
    new CoinbaseWalletAdapter()
  ];

  // Create a wrapper component to access the Solana wallet context
  const WalletContextWrapper = ({ children }) => {
    const { publicKey, connected, connecting, disconnect, select, wallet, wallets } = useSolanaWallet();

    useEffect(() => {
      if (connected && publicKey) {
        const newAccounts = [publicKey.toString()];
        setAccounts(newAccounts);
        setCurrentAccount(publicKey.toString());
      } else {
        setAccounts([]);
        setCurrentAccount(null);
      }
    }, [connected, publicKey]);

    const connectWallet = useCallback(async (walletName) => {
      try {
        setLoading(true);
        setError(null);

        if (walletName) {
          const selectedWallet = wallets.find(w => w.name.toLowerCase() === walletName.toLowerCase());
          if (selectedWallet) {
            select(selectedWallet.name);
          } else {
            throw new AlebrijeError(`Wallet ${walletName} not found`, ErrorTypes.WALLET);
          }
        }

        return connected;
      } catch (err) {
        const alebrijeError = new AlebrijeError(
          'Failed to connect wallet',
          ErrorTypes.CONNECTION,
          err
        );
        setError(alebrijeError);
        throw alebrijeError;
      } finally {
        setLoading(false);
      }
    }, [connected, select, wallets]);

    const disconnectWallet = useCallback(async () => {
      try {
        setLoading(true);
        setError(null);

        await disconnect();
        setAccounts([]);
        setCurrentAccount(null);

        return true;
      } catch (err) {
        const alebrijeError = new AlebrijeError(
          'Failed to disconnect wallet',
          ErrorTypes.CONNECTION,
          err
        );
        setError(alebrijeError);
        throw alebrijeError;
      } finally {
        setLoading(false);
      }
    }, [disconnect]);

    const switchNetwork = useCallback((newNetwork) => {
      if (Object.values(WalletAdapterNetwork).includes(newNetwork)) {
        setNetwork(newNetwork);
        return true;
      }
      return false;
    }, []);

    const contextValue = {
      connected,
      connecting,
      accounts,
      currentAccount,
      loading,
      error,
      walletName: wallet?.name || null,
      availableWallets: wallets.map(w => w.name),
      connectWallet,
      disconnectWallet,
      switchNetwork,
      network
    };

    return React.cloneElement(children, { walletContext: contextValue });
  };

  return (
    <ConnectionProvider endpoint={endpoint}>
      <WalletProvider wallets={wallets} autoConnect>
        <WalletModalProvider>
          <WalletContext.Provider value={null}>
            <WalletContextWrapper>
              {children}
            </WalletContextWrapper>
          </WalletContext.Provider>
        </WalletModalProvider>
      </WalletProvider>
    </ConnectionProvider>
  );
}

// Custom hook to use the wallet context
export function useWallet() {
  const context = useContext(WalletContext) || 
                 (React.useContext && React.useContext(WalletContext)) || 
                 (window.reactProps && window.reactProps.walletContext);

  if (!context) {
    throw new Error('useWallet must be used within a WalletContextProvider');
  }

  return context;
}

// Export the WalletMultiButton component for easy access
export { WalletMultiButton };
