import React, { useState, useEffect } from 'react';
import { useWallet } from '../contexts/WalletContext';
import TokenService from '../services/token-service';
import { handleError } from '../utils/error-handler';

const BurnToken = ({ tokenAddress: propTokenAddress }) => {
  const { connected, currentAccount } = useWallet();
  const [tokenAddress, setTokenAddress] = useState(propTokenAddress || '');
  const [amount, setAmount] = useState('');
  const [tokenInfo, setTokenInfo] = useState(null);
  const [balance, setBalance] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(null);
  const [result, setResult] = useState('');

  // Load token info and balance when token address changes
  useEffect(() => {
    if (connected && currentAccount && tokenAddress) {
      loadTokenInfo();
      loadBalance();
    }
  }, [connected, currentAccount, tokenAddress]);

  const loadTokenInfo = async () => {
    try {
      const info = await TokenService.getTokenInfo(tokenAddress);
      setTokenInfo(info);
    } catch (err) {
      console.error('Error loading token info:', err);
      setTokenInfo(null);
    }
  };

  const loadBalance = async () => {
    if (!connected || !currentAccount || !tokenAddress) return;

    try {
      const balance = await TokenService.getTokenBalance(tokenAddress, currentAccount);
      setBalance(balance);
    } catch (err) {
      console.error('Error loading token balance:', err);
      setBalance(null);
    }
  };

  const handleTokenAddressChange = (e) => {
    setTokenAddress(e.target.value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!connected || !currentAccount) {
      setError('Wallet not connected');
      return;
    }

    if (!tokenAddress) {
      setError('Token address is required');
      return;
    }

    if (!amount || parseFloat(amount) <= 0) {
      setError('Amount must be greater than 0');
      return;
    }

    try {
      setLoading(true);
      setError(null);
      setSuccess(null);
      setResult('');

      const result = await TokenService.burnToken(
        tokenAddress,
        currentAccount,
        parseFloat(amount)
      );

      setSuccess(`Burn successful! ${amount} tokens permanently removed from circulation. Signature: ${result.signature}`);
      setResult(`Token burn successful! Signature: ${result.signature}`);
      setAmount('');

      // Reload balance after burn
      setTimeout(() => {
        loadBalance();
      }, 2000);
    } catch (err) {
      console.error('Burn error:', err);
      setError(handleError(err).message);
      setResult(handleError(err).message);
    } finally {
      setLoading(false);
    }
  };

  const handleMaxAmount = () => {
    if (balance !== null) {
      setAmount(balance.toString());
    }
  };

  return (
    <div className="burn-token-container p-4 border rounded">
      <h3>Burn Tokens</h3>
      <div className="alert alert-warning">
        <strong>Warning:</strong> Burning tokens permanently removes them from circulation. This action cannot be undone.
      </div>

      <form onSubmit={handleSubmit}>
        <div className="mb-3">
          <label className="form-label">Token Address</label>
          <input 
            type="text" 
            className="form-control" 
            value={tokenAddress} 
            onChange={handleTokenAddressChange}
            placeholder="Enter the mint address of the token you want to burn"
          />
        </div>

        {tokenInfo && (
          <div className="card mb-3">
            <div className="card-body">
              <h5 className="card-title">{tokenInfo.name}</h5>
              <h6 className="card-subtitle mb-2 text-muted">{tokenInfo.symbol}</h6>
              <div className="mb-2">
                <small className="text-muted">Decimals</small>
                <p>{tokenInfo.decimals}</p>
              </div>
              <div>
                <small className="text-muted">Your Balance</small>
                <p className="mb-0">
                  {balance !== null ? (
                    `${balance} ${tokenInfo.symbol}`
                  ) : (
                    'Loading balance...'
                  )}
                </p>
              </div>
            </div>
          </div>
        )}

        {!connected && (
          <div className="alert alert-warning">
            Please connect your wallet to burn tokens.
          </div>
        )}

        <div className="mb-3">
          <label className="form-label">Amount to Burn</label>
          <div className="input-group">
            <input 
              type="number" 
              className="form-control" 
              value={amount} 
              onChange={(e) => setAmount(e.target.value)}
              placeholder="Enter amount to burn"
              step="any"
              min="0"
              disabled={!connected}
            />
            <span className="input-group-text">
              {tokenInfo ? tokenInfo.symbol : 'Tokens'}
            </span>
            <button 
              type="button" 
              className="btn btn-outline-secondary" 
              onClick={handleMaxAmount}
              disabled={!connected || balance === null}
            >
              Max
            </button>
          </div>
        </div>

        {error && (
          <div className="alert alert-danger mb-3">
            {error}
          </div>
        )}

        {success && (
          <div className="alert alert-success mb-3">
            {success}
          </div>
        )}

        <button
          type="submit"
          className="btn btn-danger w-100"
          disabled={loading || !connected || !tokenInfo || !amount}
        >
          {loading ? (
            <>
              <span className="spinner-border spinner-border-sm me-2" role="status" aria-hidden="true"></span>
              Processing...
            </>
          ) : (
            `Burn ${tokenInfo ? tokenInfo.symbol : 'Tokens'}`
          )}
        </button>
      </form>
      {result && <div className="result">{result}</div>}
    </div>
  );
};

export default BurnToken;
