import React, { useState, useEffect } from 'react';
import { useWallet } from '../contexts/WalletContext';
import TokenService, { burnTokens } from '../services/token-service';

const BurnToken = ({ tokenAddress: propTokenAddress }) => {
  const { connected, currentAccount, publicKey, connection } = useWallet();
  const [tokenAddress, setTokenAddress] = useState(propTokenAddress || '');
  const [amount, setAmount] = useState('');
  const [tokenInfo, setTokenInfo] = useState(null);
  const [balance, setBalance] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(null);

  useEffect(() => {
    if (connected && currentAccount && tokenAddress) {
      loadTokenInfo();
      loadBalance();
    }
  }, [connected, currentAccount, tokenAddress]);

  useEffect(() => {
    if (propTokenAddress) {
      setTokenAddress(propTokenAddress);
    }
  }, [propTokenAddress]);

  const loadTokenInfo = async () => {
    if (!tokenAddress) return;

    try {
      setError(null);
      if (isNativeToken(tokenAddress)) {
        setTokenInfo({
          name: "Solana",
          symbol: "SOL",
          decimals: 9,
          address: "native"
        });
        return;
      }

      const info = await TokenService.getTokenInfo(tokenAddress);
      setTokenInfo(info);
    } catch (err) {
      console.error('Error loading token info:', err);
      setTokenInfo(null);
      setError(`Error loading token info: ${err.message}`);
    }
  };

  const loadBalance = async () => {
    if (!connected || !currentAccount || !tokenAddress) return;

    try {
      if (isNativeToken(tokenAddress)) {
        const balance = await TokenService.getNativeBalance(currentAccount);
        setBalance(balance);
        return;
      }

      const balance = await TokenService.getTokenBalance(tokenAddress, currentAccount);
      setBalance(balance);
    } catch (err) {
      console.error('Error loading token balance:', err);
      setBalance(null);
      setError(`Error loading token balance: ${err.message}`);
    }
  };

  const isNativeToken = (address) => {
    return address.toLowerCase() === 'native' || address === '11111111111111111111111111111111';
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!connected || !publicKey) {
      setError('Wallet not connected');
      return;
    }

    if (!tokenAddress) {
      setError('Token address is required');
      return;
    }

    if (!amount || parseFloat(amount) <= 0) {
      setError('Amount must be greater than 0');
      return;
    }

    try {
      setLoading(true);
      setError(null);
      setSuccess(null);

      const walletAdapter = window.solana || window.solflare;

      if (!walletAdapter) {
        throw new Error('No compatible wallet found');
      }

      const wallet = {
        publicKey: publicKey,
        sendTransaction: async (tx) => {
          if (window.solana?.isPhantom) {
            const signedTx = await window.solana.signTransaction(tx);
            const signature = await connection.sendRawTransaction(signedTx.serialize());
            return signature;
          }
          else if (window.solflare) {
            const signedTx = await window.solflare.signTransaction(tx);
            const signature = await connection.sendRawTransaction(signedTx.serialize());
            return signature;
          }
          throw new Error('No compatible wallet found');
        }
      };

      const signature = await burnTokens(
        wallet,
        tokenAddress,
        parseFloat(amount)
      );

      setSuccess('Burn successful');
      setAmount('');

      setTimeout(() => {
        loadBalance();
      }, 2000);
    } catch (err) {
      console.error('Burn error:', err);
      setError(err.message || 'Burn failed');
    } finally {
      setLoading(false);
    }
  };

  const handleMaxAmount = () => {
    if (balance !== null) {
      setAmount(balance.toString());
    }
  };

  const canBurn = connected && ((tokenInfo !== null) || isNativeToken(tokenAddress)) && amount && parseFloat(amount) > 0;

  return (
    <div className="card">
      <div className="card-body">
        <h5 className="card-title">Burn {tokenAddress === 'native' ? 'SOL' : 'Tokens'}</h5>

        {error && <div className="alert alert-danger">{error}</div>}
        {success && <div className="alert alert-success">{success}</div>}

        <div className="mb-4">
          <div className="d-flex justify-content-between align-items-center">
            <div>
              <strong>Balance</strong>
              <div className="text-muted">{balance !== null ? `${balance} ${tokenAddress === 'native' ? 'SOL' : 'tokens'}` : 'Loading...'}</div>
            </div>
          </div>
        </div>

        <form onSubmit={handleSubmit}>
          <div className="mb-4">
            <label className="form-label">Amount</label>
            <div className="input-group">
              <input
                type="number"
                className="form-control"
                value={amount}
                onChange={(e) => setAmount(e.target.value)}
                placeholder="Enter amount to burn"
                step="any"
                min="0"
                disabled={loading}
              />
              <span className="input-group-text">{tokenAddress === 'native' ? 'SOL' : 'tokens'}</span>
              <button
                type="button"
                className="btn btn-outline-secondary"
                onClick={handleMaxAmount}
                disabled={loading || balance === null}
              >
                MAX
              </button>
            </div>
          </div>

          <button
            type="submit"
            className="btn btn-danger w-100"
            disabled={loading || !canBurn}
          >
            {loading ? 'Processing...' : 'Burn'}
          </button>
        </form>
      </div>
    </div>
  );
};

export default BurnToken;
