import React, { useState, useEffect } from 'react';
import { useWallet } from '../contexts/WalletContext';
import TokenService from '../services/token-service';

const TransactionHistory = ({ tokenAddress }) => {
  const { connected, currentAccount } = useWallet();
  const [transactions, setTransactions] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [selectedTransaction, setSelectedTransaction] = useState(null);

  useEffect(() => {
    if (connected && currentAccount) {
      loadTransactions();
    }
  }, [connected, currentAccount, tokenAddress]);

  const loadTransactions = async () => {
    if (!currentAccount) return;

    try {
      setLoading(true);
      setError(null);

      let txs;
      if (tokenAddress && tokenAddress !== 'native') {
        txs = await TokenService.getTokenTransactions(tokenAddress, currentAccount);
      } else {
        txs = await TokenService.getAccountTransactions(currentAccount);
      }

      setTransactions(txs);
    } catch (err) {
      console.error('Failed to load transactions:', err);
      setError('Failed to load transaction history. Please try again later.');
    } finally {
      setLoading(false);
    }
  };

  const formatDate = (timestamp) => {
    const date = new Date(timestamp * 1000);
    return date.toLocaleString();
  };

  const formatAmount = (amount, decimals = 9) => {
    return (amount / Math.pow(10, decimals)).toFixed(decimals);
  };

  const getTransactionTypeLabel = (type) => {
    switch (type) {
      case 'send':
        return <span className="badge bg-danger">Sent</span>;
      case 'receive':
        return <span className="badge bg-success">Received</span>;
      case 'swap':
        return <span className="badge bg-info">Swap</span>;
      default:
        return <span className="badge bg-secondary">Other</span>;
    }
  };

  const handleViewDetails = (tx) => {
    setSelectedTransaction(tx);
  };

  const handleCloseDetails = () => {
    setSelectedTransaction(null);
  };

  if (!connected) {
    return (
      <div className="transaction-history-container p-4 border rounded">
        <h3>Transaction History</h3>
        <div className="alert alert-warning">
          <p className="mb-0">Please connect your wallet to view transaction history</p>
        </div>
      </div>
    );
  }

  return (
    <div className="transaction-history-container p-4 border rounded">
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h3>Transaction History</h3>
        <button 
          className="btn btn-sm btn-outline-primary" 
          onClick={loadTransactions}
          disabled={loading}
        >
          {loading ? (
            <>
              <span className="spinner-border spinner-border-sm me-2" role="status" aria-hidden="true"></span>
              Loading...
            </>
          ) : (
            <><i className="bi bi-arrow-clockwise me-1"></i> Refresh</>
          )}
        </button>
      </div>

      {error && (
        <div className="alert alert-danger mb-4">
          <p className="mb-0">{error}</p>
        </div>
      )}

      {loading && transactions.length === 0 ? (
        <div className="d-flex justify-content-center my-5">
          <div className="spinner-border text-primary" role="status">
            <span className="visually-hidden">Loading...</span>
          </div>
        </div>
      ) : transactions.length === 0 ? (
        <div className="alert alert-info">
          <p className="mb-0">No transactions found for this account{tokenAddress ? ' and token' : ''}.</p>
        </div>
      ) : (
        <div className="table-responsive">
          <table className="table table-hover">
            <thead>
              <tr>
                <th>Date</th>
                <th>Type</th>
                <th>Amount</th>
                <th>Status</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {transactions.map((tx) => (
                <tr key={tx.signature}>
                  <td>{formatDate(tx.timestamp)}</td>
                  <td>{getTransactionTypeLabel(tx.type)}</td>
                  <td>
                    {formatAmount(tx.amount, tx.decimals)} {tx.symbol}
                  </td>
                  <td>
                    {tx.status === 'confirmed' ? (
                      <span className="badge bg-success">Confirmed</span>
                    ) : (
                      <span className="badge bg-warning text-dark">Pending</span>
                    )}
                  </td>
                  <td>
                    <button
                      className="btn btn-sm btn-outline-secondary"
                      onClick={() => handleViewDetails(tx)}
                    >
                      Details
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}

      {selectedTransaction && (
        <div className="modal fade show" style={{ display: 'block' }} tabIndex="-1">
          <div className="modal-dialog">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title">Transaction Details</h5>
                <button type="button" className="btn-close" onClick={handleCloseDetails}></button>
              </div>
              <div className="modal-body">
                <div className="mb-3">
                  <h6>Transaction Hash</h6>
                  <div className="input-group">
                    <input
                      type="text"
                      className="form-control form-control-sm"
                      value={selectedTransaction.signature}
                      readOnly
                    />
                    <button
                      className="btn btn-outline-secondary btn-sm"
                      onClick={() => navigator.clipboard.writeText(selectedTransaction.signature)}
                    >
                      Copy
                    </button>
                  </div>
                </div>

                <div className="row mb-3">
                  <div className="col-6">
                    <h6>Date</h6>
                    <p>{formatDate(selectedTransaction.timestamp)}</p>
                  </div>
                  <div className="col-6">
                    <h6>Status</h6>
                    <p>
                      {selectedTransaction.status === 'confirmed' ? (
                        <span className="badge bg-success">Confirmed</span>
                      ) : (
                        <span className="badge bg-warning text-dark">Pending</span>
                      )}
                    </p>
                  </div>
                </div>

                <div className="mb-3">
                  <h6>Type</h6>
                  <p>{getTransactionTypeLabel(selectedTransaction.type)}</p>
                </div>

                <div className="mb-3">
                  <h6>Amount</h6>
                  <p>
                    {formatAmount(selectedTransaction.amount, selectedTransaction.decimals)} {selectedTransaction.symbol}
                  </p>
                </div>

                {selectedTransaction.from && (
                  <div className="mb-3">
                    <h6>From</h6>
                    <div className="input-group">
                      <input
                        type="text"
                        className="form-control form-control-sm"
                        value={selectedTransaction.from}
                        readOnly
                      />
                      <button
                        className="btn btn-outline-secondary btn-sm"
                        onClick={() => navigator.clipboard.writeText(selectedTransaction.from)}
                      >
                        Copy
                      </button>
                    </div>
                  </div>
                )}

                {selectedTransaction.to && (
                  <div className="mb-3">
                    <h6>To</h6>
                    <div className="input-group">
                      <input
                        type="text"
                        className="form-control form-control-sm"
                        value={selectedTransaction.to}
                        readOnly
                      />
                      <button
                        className="btn btn-outline-secondary btn-sm"
                        onClick={() => navigator.clipboard.writeText(selectedTransaction.to)}
                      >
                        Copy
                      </button>
                    </div>
                  </div>
                )}

                <div className="mb-3">
                  <h6>Explorer</h6>
                  <a 
                    href={`https://explorer.solana.com/tx/${selectedTransaction.signature}`} 
                    target="_blank" 
                    rel="noopener noreferrer"
                    className="btn btn-sm btn-outline-primary"
                  >
                    View on Solana Explorer
                  </a>
                </div>
              </div>
              <div className="modal-footer">
                <button type="button" className="btn btn-secondary" onClick={handleCloseDetails}>
                  Close
                </button>
              </div>
            </div>
          </div>
          <div className="modal-backdrop fade show"></div>
        </div>
      )}
    </div>
  );
};

export default TransactionHistory;
