import React from "react";
import QRCode from "qrcode.react";

export const BaseQRCode = ({
  size = 200,
  data = "",
  ecl = "L",
}: {
  data: string;
  size?: number;
  ecl?: "L" | "M" | "Q" | "H";
}) => {
  return <QRCode value={data} size={size} level={ecl} />;
};
import { UR } from "@ngraveio/bc-ur";

type DecodedSuccess = {
  result: UR;
  status: ReadStatus.success;
};

type DecodedCanceled = {
  status: ReadStatus.canceled;
};

export type DecodedResult = DecodedSuccess | DecodedCanceled;

export enum ReadStatus {
  canceled = "canceled",
  success = "success",
}

export enum PlayStatus {
  canceled = "canceled",
  success = "success",
}

export type Play = (
  data: UR,
  options?: {
    refreshSpeed?: number;
    hasNext?: boolean;
    title?: string;
    description?: string;
    maxFragmentLength? : number;
  }
) => Promise<PlayStatus>;

export type Read = (
  expect: SupportedResult[],
  options?: {
    title?: string;
    description?: string;
    renderInitial?: {
      walletMode: string;
      link: string;
    };
    URTypeErrorMessage?: string;
  }
) => Promise<DecodedResult>;

export enum SupportedResult {
  UR_BYTES = "bytes",
  UR_CRYPTO_HDKEY = "crypto-hdkey",
  UR_CRYPTO_ACCOUNT = "crypto-account",
  UR_ETH_SIGN_REQUEST = "eth-sign-request",
  UR_ETH_SIGNATURE = "eth-signature",
  UR_CRYPTO_MULTI_ACCOUNTS = "crypto-multi-accounts",
  UR_SOL_SIGN_REQUEST = "sol-sign-request",
  UR_SOL_SIGNATURE = "sol-signature",
  UR_APTOS_SIGN_REQUEST='aptos-sign-request',
  UR_APTOS_SIGNATURE = 'aptos-signature',
  UR_ARWEAVE_SIGN_REQUEST = 'arweave-sign-request',
  UR_ARWEAVE_SIGNATURE = 'arweave-signature',
  UR_ARWEAVE_CRYPTO_ACCOUNT = 'arweave-crypto-account',
  UR_COSMOS_SIGN_REQUEST = 'cosmos-sign-request',
  UR_COSMOS_SIGNATURE = 'cosmos-signature'
}
import React, { FunctionComponent } from "react";

type Props = {
  onClick: () => any;
};

const styleBase = {
  minWidth: "16rem",
  height: "3rem",
  background: "#784FFE",
  borderColor: "grey",
  borderRadius: "8px",
  borderWidth: 1,
  color: "white",
  outline: "none",
  margin: 2,
};

export const Button: FunctionComponent<Props> = (props) => {
  const { onClick, children } = props;
  return (
    <button onClick={onClick} style={styleBase}>
      {children}
    </button>
  );
};
import React, { FunctionComponent } from "react";

export const ButtonGroup: FunctionComponent = (props) => {
  return (
    <div
      style={{
        width: "100%",
        marginTop: "1rem",
        display: "flex",
        flexDirection: "row-reverse",
        alignItems: "center",
        justifyContent: "center",
      }}
    >
      {props.children}
    </div>
  );
};
import React, { useEffect, useMemo, useState } from "react";
import { interval } from "rxjs";
import { BaseQRCode } from "../components/BaseQRCode";
import { Play, PlayStatus } from "../types";
import { EventEmitter } from "events";
import { Button } from "../components/Button";
import { ButtonGroup } from "../components/ButtonGroup";
import { UR, UREncoder } from "@ngraveio/bc-ur";

const DEFAULT_SPEED = 100;

const DEFAULT_MAX_FRAGMENT_LENGTH = 400;

const DEFAULT_UR = new UR(Buffer.from("NO DATA", "utf-8"));

export const useAnimatedQRCodePlayer = (): [JSX.Element, { play: Play }] => {
  const [data, setData] = useState<UR>(DEFAULT_UR);
  const [shouldShow, setShouldShow] = useState(false);

  const [refreshSpeed, setRefreshSpeed] = useState(DEFAULT_SPEED);
  const [maxFragmentLength, setMaxFragmentLength] = useState(DEFAULT_MAX_FRAGMENT_LENGTH)
  const [hasNext, setHasNext] = useState(false);
  const [title, setTitle] = useState<string | null>(null);
  const [description, setDescription] = useState<string | null>(null);

  const urEncoder = useMemo(() => new UREncoder(data, maxFragmentLength), [data]);

  const [qr, setQR] = useState<string>(urEncoder.nextPart());

  const ee = useMemo(() => new EventEmitter(), []);
  const reset = () => {
    setData(DEFAULT_UR);
    setShouldShow(false);
    setRefreshSpeed(DEFAULT_SPEED);
  };

  useEffect(() => {
    if (urEncoder.cbor.toString("hex") !== DEFAULT_UR.cbor.toString("hex")) {
      setShouldShow(true);
    }
  }, [urEncoder]);

  useEffect(() => {
    const subscribe = interval(refreshSpeed).subscribe(() => {
      setQR(urEncoder.nextPart());
    });
    return () => {
      subscribe.unsubscribe();
    };
  }, [refreshSpeed, urEncoder]);

  const finish = () => {
    ee.emit("finish", true);
  };

  const element = shouldShow ? (
    <div
      style={{
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
      }}
    >
      {title && (
        <p style={{ fontSize: "1.25rem", fontWeight: "bold" }}>{title}</p>
      )}
      <BaseQRCode size={288} data={qr} />
      {description && (
        <p style={{ fontSize: "1rem", textAlign: "center" }}>{description}</p>
      )}
      <ButtonGroup>
        <Button onClick={finish}>{hasNext ? "Scan Keystone" : "Finish"}</Button>
      </ButtonGroup>
    </div>
  ) : (
    <div></div>
  );

  return [
    element,
    {
      play: (data, options) => {
        return new Promise((resolve) => {
          setData(data);
          if (options) {
            options.refreshSpeed && setRefreshSpeed(options.refreshSpeed);
            options.hasNext && setHasNext(options.hasNext);
            options.title && setTitle(options.title);
            options.description && setDescription(options.description);
            options.maxFragmentLength && setMaxFragmentLength(options.maxFragmentLength)
          }
          ee.once("finish", () => {
            reset();
            resolve(PlayStatus.success);
          });
        });
      },
    },
  ];
};
export class URTypeError extends Error {
  constructor(msg: string) {
    super(msg);

    // Set the prototype explicitly.
    Object.setPrototypeOf(this, URTypeError.prototype);
  }
}
import React from "react";

export const LoadingSpinner = () => {
  return (
    <svg
      xmlns="http://www.w3.org/2000/svg"
      style={{
        margin: "auto",
        display: "block",
        shapeRendering: "auto",
        animationPlayState: "running",
        animationDelay: "0s",
      }}
      width="200px"
      height="200px"
      viewBox="0 0 100 100"
      preserveAspectRatio="xMidYMid"
    >
      <circle
        cx="50"
        cy="50"
        fill="none"
        stroke="#9b9b9b"
        strokeWidth="7"
        r="35"
        strokeDasharray="164.93361431346415 56.97787143782138"
        style={{ animationPlayState: "running", animationDelay: "0s" }}
      >
        <animateTransform
          attributeName="transform"
          type="rotate"
          repeatCount="indefinite"
          dur="1s"
          values="0 50 50;360 50 50"
          keyTimes="0;1"
          style={{ animationPlayState: "running", animationDelay: "0s" }}
        ></animateTransform>
      </circle>
    </svg>
  );
};
import React, { useMemo, useState, Suspense } from "react";
import { EventEmitter } from "events";
import { Button } from "../components/Button";
import { URTypeError } from "../error";

import { Read, SupportedResult } from "../types";
import { ButtonGroup } from "../components/ButtonGroup";
import { LoadingSpinner } from "../components/LoadingSpinner";
import { URDecoder } from "@ngraveio/bc-ur";

const QrReader = React.lazy(() => import("react-qr-reader"));

export interface URQRCodeData {
  total: number;
  index: number;
  data: string;
}

let URTypeErrorMessage = "";

export const useAnimatedQRCodeReader = (): [
  JSX.Element,
  { read: Read; cameraReady: boolean }
] => {
  const [cameraReady, setCameraReady] = useState<boolean>(false);
  const [expectTypes, setExpectTypes] = useState<SupportedResult[]>([]);
  const [urDecoder, setURDecoder] = useState(new URDecoder());
  const [error, setError] = useState("");
  const ee = useMemo(() => new EventEmitter(), []);
  const [title, setTitle] = useState<string | null>(null);
  const [progress, setProgress] = useState(0);
  const [description, setDescription] = useState<string | null>(null);
  const reset = () => {
    setURDecoder(new URDecoder());
    setError("");
  };

  const processQRCode = (qr: string, errorMessgeOnURType: string) => {
    processUR(qr, errorMessgeOnURType);
  };

  const handleStop = () => {
    ee.emit("read", {
      status: "canceled",
    });
  };

  const handleRetry = () => {
    reset();
  };

  const processUR = (ur: string, errorMessgeOnURType: string) => {
    try {
      if (!urDecoder.isComplete()) {
        urDecoder.receivePart(ur);
        setProgress(urDecoder.getProgress());
      } else {
        const result = urDecoder.resultUR();
        let foundExpected = false;
        expectTypes.forEach((et) => {
          if (et === result.type) {
            foundExpected = true;
            ee.emit("read", {
              result,
              status: "success",
            });
            return;
          }
        });
        if (!foundExpected)
          throw new URTypeError(
            `received ur type ${result.type}, but expected [${expectTypes.join(
              ","
            )}]`
          );
      }
    } catch (e) {
      if (e instanceof URTypeError) {
        setError(errorMessgeOnURType);
      } else {
        setError((e as Error).message);
      }
    }
  };

  const element = (
    <div
      style={{
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
      }}
    >
      {title && (
        <p style={{ fontSize: "1.25rem", fontWeight: "bold" }}>{title}</p>
      )}
      {description && (
        <p style={{ fontSize: "1rem", textAlign: "center" }}>{description}</p>
      )}
      <Suspense fallback={<div />}>
        <div style={{ position: "relative", width: "100%" }}>
          {!cameraReady ? (
            <div
              style={{
                position: "absolute",
                top: "50%",
                left: "50%",
                transform: "translate(-50%, -50%)",
              }}
            >
              <LoadingSpinner />
            </div>
          ) : null}
          <QrReader
            onScan={(data: any) => {
              if (data) {
                setCameraReady(true);
                processQRCode(data, URTypeErrorMessage);
              }
            }}
            delay={100}
            style={{ width: "100%" }}
            onError={(e) => {
              setError(e.message);
            }}
          />
        </div>
      </Suspense>
      <p>{(progress * 100).toFixed(0)} %</p>
      {error && <p style={{ color: "red", fontSize: "1rem" }}>{error}</p>}
      <ButtonGroup>
        <Button onClick={handleStop}>Close</Button>
        {error && <Button onClick={handleRetry}>Retry</Button>}
      </ButtonGroup>
    </div>
  );

  return [
    element,
    {
      read: (expect, options) => {
        return new Promise((resolve) => {
          setExpectTypes(expect);
          if (options) {
            options.title && setTitle(options.title);
            options.description && setDescription(options.description);
            URTypeErrorMessage = options.URTypeErrorMessage
              ? options.URTypeErrorMessage
              : "";
          }
          ee.once("read", (result) => {
            reset();
            resolve(result);
          });
        });
      },
      cameraReady,
    },
  ];
};
