import React, { useState, useEffect } from 'react';
import { useWallet } from '../contexts/WalletContext';
import TokenService from '../services/token-service';

const TransactionHistory = () => {
  const { connected, currentAccount, network } = useWallet();
  const [transactions, setTransactions] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [selectedTx, setSelectedTx] = useState(null);
  const [showModal, setShowModal] = useState(false);

  useEffect(() => {
    if (connected && currentAccount) {
      loadTransactions();
    } else {
      setTransactions([]);
    }
  }, [connected, currentAccount, network]);

  const loadTransactions = async () => {
    if (!connected || !currentAccount) return;

    try {
      setLoading(true);
      setError(null);

      const txs = await TokenService.getAccountTransactions(currentAccount);
      setTransactions(txs);
    } catch (err) {
      console.error('Error loading transactions:', err);
      setError('Failed to load transaction history');
    } finally {
      setLoading(false);
    }
  };

  const handleViewDetails = (tx) => {
    setSelectedTx(tx);
    setShowModal(true);
  };

  const closeModal = (e) => {
    if (e) {
      e.stopPropagation();
    }
    setShowModal(false);
    setSelectedTx(null);
  };

  const getExplorerUrl = (signature) => {
    const baseUrl = 'https://explorer.solana.com/tx/';
    const clusterParam = network === 'mainnet-beta' ? '' : `?cluster=${network}`;
    return `${baseUrl}${signature}${clusterParam}`;
  };

  const handleModalClick = (e) => {
    e.stopPropagation();
  };

  return (
    <div className="transaction-history-container p-4 border rounded">
      <h3>Transaction History</h3>

      {!connected ? (
        <div className="alert alert-warning">
          Please connect your wallet to view transaction history.
        </div>
      ) : (
        <>
          <div className="mb-3 d-flex justify-content-between align-items-center">
            <span>{transactions.length} transactions found</span>
            <button 
              className="btn btn-sm btn-outline-primary" 
              onClick={loadTransactions}
              disabled={loading}
            >
              {loading ? 'Loading...' : 'Refresh'}
            </button>
          </div>

          {error && (
            <div className="alert alert-danger mb-3">
              {error}
            </div>
          )}

          {transactions.length === 0 && !loading && !error ? (
            <div className="alert alert-info">
              No transactions found for this account.
            </div>
          ) : (
            <div className="table-responsive">
              <table className="table table-hover">
                <thead>
                  <tr>
                    <th>Date</th>
                    <th>Type</th>
                    <th>Amount</th>
                    <th>Status</th>
                    <th>Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {transactions.map((tx) => (
                    <tr key={tx.signature}>
                      <td>{new Date(tx.timestamp * 1000).toLocaleString()}</td>
                      <td>{tx.type}</td>
                      <td>{tx.amount} {tx.symbol}</td>
                      <td>
                        <span className={`badge ${tx.status === 'confirmed' ? 'bg-success' : 'bg-warning'}`}>
                          {tx.status}
                        </span>
                      </td>
                      <td>
                        <button 
                          className="btn btn-sm btn-outline-info me-2"
                          onClick={() => handleViewDetails(tx)}
                        >
                          Details
                        </button>
                        <a 
                          href={getExplorerUrl(tx.signature)} 
                          target="_blank" 
                          rel="noopener noreferrer"
                          className="btn btn-sm btn-outline-secondary"
                        >
                          Explorer
                        </a>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          )}

          {/* Transaction Details Modal */}
          {showModal && selectedTx && (
            <div className="modal show d-block" tabIndex="-1" onClick={closeModal}>
              <div className="modal-dialog" onClick={handleModalClick}>
                <div className="modal-content">
                  <div className="modal-header">
                    <h5 className="modal-title">Transaction Details</h5>
                    <button 
                      type="button" 
                      className="btn-close" 
                      onClick={closeModal}
                      aria-label="Close"
                    ></button>
                  </div>
                  <div className="modal-body">
                    <div className="mb-3">
                      <strong>Signature:</strong>
                      <div className="text-break">
                        <small>{selectedTx.signature}</small>
                      </div>
                    </div>
                    <div className="mb-3">
                      <strong>Date:</strong>
                      <div>{new Date(selectedTx.timestamp * 1000).toLocaleString()}</div>
                    </div>
                    <div className="mb-3">
                      <strong>Status:</strong>
                      <div>
                        <span className={`badge ${selectedTx.status === 'confirmed' ? 'bg-success' : 'bg-warning'}`}>
                          {selectedTx.status}
                        </span>
                      </div>
                    </div>
                    <div className="mb-3">
                      <strong>Type:</strong>
                      <div>{selectedTx.type}</div>
                    </div>
                    <div className="mb-3">
                      <strong>Amount:</strong>
                      <div>{selectedTx.amount} {selectedTx.symbol}</div>
                    </div>
                  </div>
                  <div className="modal-footer">
                    <a 
                      href={getExplorerUrl(selectedTx.signature)} 
                      target="_blank" 
                      rel="noopener noreferrer"
                      className="btn btn-outline-primary"
                    >
                      View on Explorer
                    </a>
                    <button 
                      type="button" 
                      className="btn btn-secondary" 
                      onClick={closeModal}
                    >
                      Close
                    </button>
                  </div>
                </div>
              </div>
              <div className="modal-backdrop show"></div>
            </div>
          )}
        </>
      )}
    </div>
  );
};

export default TransactionHistory;
